<script>
    const cookies = document.cookie.split('; ');
    let shopifyCookie = cookies.find(cookie => cookie.startsWith('_shopify_y='));
    shopifyCookie = shopifyCookie.split('=')[1];
    const element = document.getElementById(`Slider-template--16317651484834__recommended_for_you_BqUcpH`);
    if (shopifyCookie) {
      fetchRecommendations(shopifyCookie);
    } else {
      var checkDataLayer = setInterval(function () {
        if (window.dataLayer !== undefined) {
          if (dataLayer.some(s => s.marketing)) {
            clearInterval(checkDataLayer);
            const userId = dataLayer.find(s => s.marketing).marketing.user_id;
            fetchRecommendations(userId);
          }
        }
      }, 50);
    }
    function fetchRecommendations(userId) {
      fetch('https://api.giva.co/retail/getReco', {
        method: 'POST',
        body: JSON.stringify({
          id: userId,

        }),
        headers: {
          'Content-Type': 'application/json'
        }
      }).then(s => s.json()).then(r => {
        const prods = r.prods;
        const token = r.token;
        Promise.all(prods.map((p, index) => fetch(`${p}?view=card`).then(r => r.text()).then(res => {
          const text = res;
          const parser = new DOMParser();
          const htmlDocument = parser.parseFromString(text, 'text/html');
          const productCard = htmlDocument.documentElement.querySelector(".template-card");
          if (productCard) {
            return productCard.innerHTML.replace(/(href="[^"]*)/g, `$1?rtoken=${token}&rwidget=home&rindex=${index + 1}`);
          } else {
            return null;
          }
        }))).then(cards => {
          const categories = {};
          const finalProds = cards.filter(s => s);
          document.getElementById('reco-loader').style.display = 'none';
          document.getElementById('recommendations-slider-home').style.display = 'block';
          finalProds.forEach((prod, index) => {
            const parser = new DOMParser();
            const doc = parser.parseFromString(prod, 'text/html');
            const modifiedElement = doc.body;
            let prodType = modifiedElement.querySelector('#hidden_type').value;
            categories[prodType] = categories[prodType] ? categories[prodType] + 1 : 1;
            const finalHtml = `<li id="Slide-template--16317651484834__recommended_for_you_BqUcpH-${index + 1}" class="grid__item slider__slide scroll-trigger animate--slide-in" 
              data-cascade
              style="--animation-order: ${index + 1};"
              >${prod}</li>`;
            element.innerHTML += finalHtml;
          });
          const bannerTypes = {};
          Object.keys(categories).forEach((key) => {
            if (key.includes("Diamond") || key.includes("Gold ")) {
              bannerTypes["gold"] = bannerTypes["gold"]
                ? bannerTypes["gold"] + categories[key]
                : categories[key];
            } else if (key.includes("men")) {
              bannerTypes["men"] = bannerTypes["men"]
                ? bannerTypes["men"] + categories[key]
                : categories[key];
            } else if (key === "Necklaces" || key === "Pendant") {
              bannerTypes["pendant"] = bannerTypes["pendant"]
                ? bannerTypes["pendant"] + categories[key]
                : categories[key];
            } else if (key === "Rings") {
              bannerTypes["rings"] = bannerTypes["rings"]
                ? bannerTypes["rings"] + categories[key]
                : categories[key];
            } else if (key === "Earrings") {
              bannerTypes["earrings"] = bannerTypes["earrings"]
                ? bannerTypes["earrings"] + categories[key]
                : categories[key];
            } else if (key === "Bracelet") {
              bannerTypes["bracelet"] = bannerTypes["bracelet"]
                ? bannerTypes["bracelet"] + categories[key]
                : categories[key];
            } else if (key === "Personalised") {
              bannerTypes["personalised"] = bannerTypes["personalised"]
                ? bannerTypes["personalised"] + categories[key]
                : categories[key];
            }
          });
          const itemsTotal = Object.values(bannerTypes).reduce(
            (sum, value) => sum + value,
            0
          );
          const result = Object.keys(bannerTypes).filter(
            (key) => (bannerTypes[key] / itemsTotal) * 100 >= 30
          );
          if (
            result.length > 0 &&
            document.querySelector("#personalised_banner_container")
          ) {
            document.querySelector("#personalised_banner_container").style.display =
              "block";
            if (result[0] === "rings") {
              document.querySelector(
                "#personalised_banner_container #rings-banner"
              ).style.display = "block";
              document.querySelector(
                "#personalised_banner_container #rings-banner a"
              ).href += `?rtoken=${token}&rwidget=home&rindex=${document.querySelector(
                "#personalised_banner_container #rings-banner a"
              ).id.replace('banner-','')}`;
            } else if (result[0] === "personalised") {
              document.querySelector(
                "#personalised_banner_container #personalised-banner"
              ).style.display = "block";
              document.querySelector(
                "#personalised_banner_container #personalised-banner a"
              ).href += `?rtoken=${token}&rwidget=home&rindex=${document.querySelector(
                "#personalised_banner_container #personalised-banner a"
              ).id.replace('banner-','')}`;
            } else if (result[0] === "pendant") {
              document.querySelector(
                "#personalised_banner_container #pendant-banner"
              ).style.display = "block";
              document.querySelector(
                "#personalised_banner_container #pendant-banner a"
              ).href += `?rtoken=${token}&rwidget=home&rindex=${document.querySelector(
                "#personalised_banner_container #pendant-banner a"
              ).id.replace('banner-','')}`;
            } else if (result[0] === "men") {
              document.querySelector(
                "#personalised_banner_container #men-banner"
              ).style.display = "block";
              document.querySelector(
                "#personalised_banner_container #men-banner a"
              ).href += `?rtoken=${token}&rwidget=home&rindex=${document.querySelector(
                "#personalised_banner_container #men-banner a"
              ).id.replace('banner-','')}`;
            } else if (result[0] === "gold") {
              document.querySelector(
                "#personalised_banner_container #gold-banner"
              ).style.display = "block";
              document.querySelector(
                "#personalised_banner_container #gold-banner a"
              ).href += `?rtoken=${token}&rwidget=home&rindex=${document.querySelector(
                "#personalised_banner_container #gold-banner a"
              ).id.replace('banner-','')}`;
            } else if (result[0] === "bracelet") {
              document.querySelector(
                "#personalised_banner_container #bracelet-banner"
              ).style.display = "block";
              document.querySelector(
                "#personalised_banner_container #bracelet-banner a"
              ).href += `?rtoken=${token}&rwidget=home&rindex=${document.querySelector(
                "#personalised_banner_container #bracelet-banner a"
              ).id.replace('banner-','')}`;
            } else if (result[0] === "earrings") {
              document.querySelector(
                "#personalised_banner_container #earring-banner"
              ).style.display = "block";
              document.querySelector(
                "#personalised_banner_container #earring-banner a"
              ).href += `?rtoken=${token}&rwidget=home&rindex=${document.querySelector(
                "#personalised_banner_container #earring-banner a"
              ).id.replace('banner-','')}`;
            }
          }
        });
      }).catch(e => {
        console.log(e);
      });
    }
</script>

